// Academic Theme Main JavaScript

(function() {
  'use strict';

  // Mobile menu toggle
  function initMobileMenu() {
    const button = document.querySelector('.greedy-nav button');
    const menu = document.querySelector('.visible-links');
    
    if (button && menu) {
      button.addEventListener('click', function() {
        menu.classList.toggle('active');
      });
    }
  }

  // Smooth scrolling for anchor links
  function initSmoothScrolling() {
    const links = document.querySelectorAll('a[href^="#"]');
    
    links.forEach(link => {
      link.addEventListener('click', function(e) {
        e.preventDefault();
        
        const targetId = this.getAttribute('href').substring(1);
        const targetElement = document.getElementById(targetId);
        
        if (targetElement) {
          targetElement.scrollIntoView({
            behavior: 'smooth',
            block: 'start'
          });
        }
      });
    });
  }

  // Initialize when DOM is loaded
  document.addEventListener('DOMContentLoaded', function() {
    initMobileMenu();
    initSmoothScrolling();
  });

})();

// jQuery fallback for compatibility
if (typeof jQuery !== 'undefined') {
  (function($) {
    $(document).ready(function() {
      // Any jQuery-specific functionality can go here
    });
  })(jQuery);
}
